mapper.select:
select

from m_mbr_adjust_order mao
where mao.id = #{id}

select

from m_mbr_adjust_order mao
where mao.order_no = #{orderNo}

select

from m_mbr_adjust_order mao


        and mao.winery_adjust_wine_id = #{wineryAdjustWineId}


        and mao.mbr_id = #{mbrId}


        and mao.winery_id = #{wineryId}


        and mao.pay_mode = #{payMode}


        and mao.pay_total_amt = #{payTotalAmt}


        and mao.pay_real_amt = #{payRealAmt}


        and mao.print_img = #{printImg}


        and mao.order_status = #{orderStatus}


        and mao.transaction_no = #{transactionNo}


        and mao.order_no = #{orderNo}


        and mao.pay_date = #{payDate}


        and mao.create_date = #{createDate}


        and mao.modify_date = #{modifyDate}
