mapper.select:
select 

from m_mbr_adjust_order_record maor
where maor.id = #{id}

select 

from m_mbr_adjust_order_record maor


        and maor.mbr_adjust_order_id = #{mbrAdjustOrderId}


        and maor.mbr_id = #{mbrId}


        and maor.winery_id = #{wineryId}


        and maor.pay_total_amt = #{payTotalAmt}


        and maor.pay_real_amt = #{payRealAmt}


        and maor.order_status = #{orderStatus}


        and maor.create_date = #{createDate}


        and maor.modify_date = #{modifyDate}
