mapper.select:
select

from m_mbr_bill_record mbr
where mbr.id = #{id}

select

from m_mbr_bill_record mbr


        and mbr.pk_id = #{pkId}


        and mbr.mbr_id = #{mbrId}


        and mbr.winery_id = #{wineryId}


        and mbr.sign_type = #{signType}


        and mbr.bill_type = #{billType}


        and mbr.bill_remark = #{billRemark}


        and mbr.bill_amt = #{billAmt}


        and mbr.create_date = #{createDate}


        and mbr.modify_date = #{modifyDate}
