mapper.select:
select 

from m_mbr_cart cart
where cart.id = #{id}

select 

from m_mbr_cart cart

    
        and cart.winery_id = #{wineryId}
    
    
        and cart.mbr_id = #{mbrId}
    
    
        and cart.prod_id = #{prodId}
    
    
        and cart.prod_sku_id = #{prodSkuId}
    
    
        and cart.prod_sku_cnt = #{prodSkuCnt}
    
    
        and cart.create_date = #{createDate}
    
    
        and cart.modify_date = #{modifyDate}
