mapper.select:
select

from m_mbr_integral_record mir
where mir.id = #{id}

select

from m_mbr_integral_record mir


        and mir.pk_id = #{pkId}


        and mir.winery_id = #{wineryId}


        and mir.mbr_id = #{mbrId}


        and mir.action_type = #{actionType}


        and mir.sign_type = #{signType}


        and mir.integral_value = #{integralValue}


        and mir.latest_point = #{latestPoint}


        and mir.create_date = #{createDate}


        and mir.modify_date = #{modifyDate}
