mapper.select:
select

from m_mbr_prod_order_item mpoi
where mpoi.id = #{id}

select

from m_mbr_prod_order_item mpoi
where mpoi.mbr_prod_order_id = #{mbrProdOrderId}

select

from m_mbr_prod_order_item mpoi


        and mpoi.mbr_prod_order_id = #{mbrProdOrderId}


        and mpoi.winery_id = #{wineryId}


        and mpoi.mbr_id = #{mbrId}


        and mpoi.prod_sku_id = #{prodSkuId}


        and mpoi.prod_sku_cnt = #{prodSkuCnt}


        and mpoi.sku_name = #{skuName}


        and mpoi.sku_market_price = #{skuMarketPrice}


        and mpoi.sku_sell_price = #{skuSellPrice}


        and mpoi.sku_mbr_price = #{skuMbrPrice}


        and mpoi.is_integral = #{isIntegral}


        and mpoi.integral_amt = #{integralAmt}


        and mpoi.integral_cnt = #{integralCnt}


        and mpoi.create_date = #{createDate}


        and mpoi.modify_date = #{modifyDate}
