mapper.select:
select

from m_mbr_prod_order mpo
where mpo.id = #{id}

select

from m_mbr_prod_order mpo

    
        and mpo.winery_id = #{wineryId}
    
    
        and mpo.mbr_id = #{mbrId}
    
    
        and mpo.pay_mode = #{payMode}
    
    
        and mpo.prod_total_cnt = #{prodTotalCnt}
    
    
        and mpo.pay_total_amt = #{payTotalAmt}
    
    
        and mpo.pay_real_amt = #{payRealAmt}
    
    
        and mpo.pay_integral_cnt = #{payIntegralCnt}
    
    
        and mpo.order_status = #{orderStatus}
    
    
        and mpo.shipping_detail_addr = #{shippingDetailAddr}
    
    
        and mpo.shipping_province_id = #{shippingProvinceId}
    
    
        and mpo.shipping_city_id = #{shippingCityId}
    
    
        and mpo.shipping_county_id = #{shippingCountyId}
    
    
        and mpo.shipping_person_name = #{shippingPersonName}
    
    
        and mpo.shipping_person_phone = #{shippingPersonPhone}
    
    
        and mpo.order_no = #{orderNo}
    
    
        and mpo.transaction_no = #{transactionNo}
    
    
        and mpo.pay_date = #{payDate}
    
    
        and mpo.create_date = #{createDate}
    
    
        and mpo.modify_date = #{modifyDate}

select

from m_mbr_prod_order mpo
where mpo.order_no = #{orderNo}
