mapper.select:
select

from m_mbr_prod_order mir
where mir.id = #{id}

select

from m_mbr_prod_order mir

    
        and mir.winery_id = #{wineryId}
    
    
        and mir.mbr_id = #{mbrId}
    
    
        and mir.pay_mode = #{payMode}
    
    
        and mir.prod_total_cnt = #{prodTotalCnt}
    
    
        and mir.pay_total_amt = #{payTotalAmt}
    
    
        and mir.pay_real_amt = #{payRealAmt}
    
    
        and mir.pay_integral_cnt = #{payIntegralCnt}
    
    
        and mir.order_status = #{orderStatus}
    
    
        and mir.shipping_detail_addr = #{shippingDetailAddr}
    
    
        and mir.shipping_province_id = #{shippingProvinceId}
    
    
        and mir.shipping_city_id = #{shippingCityId}
    
    
        and mir.shipping_county_id = #{shippingCountyId}
    
    
        and mir.shipping_person_name = #{shippingPersonName}
    
    
        and mir.shipping_person_phone = #{shippingPersonPhone}
    
    
        and mir.order_no = #{orderNo}
    
    
        and mir.transaction_no = #{transactionNo}
    
    
        and mir.pay_date = #{payDate}
    
    
        and mir.create_date = #{createDate}
    
    
        and mir.modify_date = #{modifyDate}

select

from m_mbr_prod_order mpo
where mpo.order_no = #{orderNo}
