mapper.select:
select

from m_mbr_prod_order_record mpor
where mpor.id = #{id}

select

from m_mbr_prod_order_record mpor


        and mpor.mbr_prod_order_id = #{mbrProdOrderId}


        and mpor.order_status = #{orderStatus}


        and mpor.order_remark = #{orderRemark}


        and mpor.create_date = #{createDate}


        and mpor.modify_date = #{modifyDate}
