mapper.select:
select

from m_mbr_recharge_order mro
where mro.id = #{id}

select

from m_mbr_recharge_order mro


        and mro.winery_id = #{wineryId}


        and mro.mbr_id = #{mbrId}


        and mro.pay_total_amt = #{payTotalAmt}


        and mro.pay_real_amt = #{payRealAmt}


        and mro.order_status = #{orderStatus}


        and mro.pay_date = #{payDate}


        and mro.create_date = #{createDate}


        and mro.modify_date = #{modifyDate}
