mapper.select:
select

from m_mbr_recharge_order_record mror
where mror.id = #{id}

mapper.insert:
insert into m_mbr_recharge_order_record (id, mbr_recharge_order_id, order_status, create_date, modify_date
    )
values (#{id}, #{mbrRechargeOrderId}, #{orderStatus}, #{createDate}, #{modifyDate}
    )

mapper.select:
select

from m_mbr_recharge_order_record mror


        and mror.mbr_recharge_order_id = #{mbrRechargeOrderId}


        and mror.order_status = #{orderStatus}


        and mror.create_date = #{createDate}


        and mror.modify_date = #{modifyDate}
