mapper.select:
select

from m_mbr_sight_sign mss
where mss.id = #{id}

select

from m_mbr_sight_sign mss


        and mss.winery_id = #{wineryId}


        and mss.winery_sight_id = #{winerySightId}


        and mss.mbr_id = #{mbrId}


        and mss.sign_date = #{signDate}


        and mss.create_date = #{createDate}


        and mss.modify_date = #{modifyDate}
