mapper.select:
select 

from m_mbr_store_order_item mbrStoreOrderItem


        and mbrStoreOrderItem.mbr_store_order_id = #{mbrStoreOrderId}


        and mbrStoreOrderItem.winery_id = #{wineryId}


        and mbrStoreOrderItem.mbr_id = #{mbrId}


        and mbrStoreOrderItem.prod_sku_id = #{prodSkuId}


        and mbrStoreOrderItem.prod_sku_cnt = #{prodSkuCnt}


        and mbrStoreOrderItem.sku_name = #{skuName}


        and mbrStoreOrderItem.sku_market_price = #{skuMarketPrice}


        and mbrStoreOrderItem.sku_sell_price = #{skuSellPrice}


        and mbrStoreOrderItem.sku_mbr_price = #{skuMbrPrice}


        and mbrStoreOrderItem.is_integral = #{isIntegral}


        and mbrStoreOrderItem.integral_amt = #{integralAmt}


        and mbrStoreOrderItem.integral_cnt = #{integralCnt}


        and mbrStoreOrderItem.sku_weight = #{skuWeight}


        and mbrStoreOrderItem.sku_capacity = #{skuCapacity}


        and mbrStoreOrderItem.create_date = #{createDate}


        and mbrStoreOrderItem.modify_date = #{modifyDate}
