mapper.select:
select 

from m_mbr_store_order_record msor
where msor.id = #{id}

select 

from m_mbr_store_order_record msor


        and msor.mbr_store_order_id = #{mbrStoreOrderId}


        and msor.order_status = #{orderStatus}


        and msor.order_remark = #{orderRemark}


        and msor.create_date = #{createDate}


        and msor.modify_date = #{modifyDate}
