mapper.insert:
insert into m_mbr_take_order (id, mbr_store_order_id, winery_id, mbr_id, take_weight, take_capacity, 
    delivery_mode, package_price, total_package_amt, pay_total_amt, pay_real_amt, 
    order_status, shipping_detail_addr, shipping_province_id, shipping_city_id, 
    shipping_county_id, transaction_no, order_no, create_date, modify_date
    )
values (#{id}, #{mbrStoreOrderId}, #{wineryId}, #{mbrId}, #{takeWeight}, #{takeCapacity}, 
    #{deliveryMode}, #{packagePrice}, #{totalPackageAmt}, #{payTotalAmt}, #{payRealAmt}, 
    #{orderStatus}, #{shippingDetailAddr}, #{shippingProvinceId}, #{shippingCityId}, 
    #{shippingCountyId}, #{transactionNo}, #{orderNo}, #{createDate}, #{modifyDate}
    )

mapper.select:
select 

from m_mbr_take_order mbrTakeOrder


        and mbrTakeOrder.mbr_store_order_id = #{mbrStoreOrderId}


        and mbrTakeOrder.winery_id = #{wineryId}


        and mbrTakeOrder.mbr_id = #{mbrId}


        and mbrTakeOrder.take_weight = #{takeWeight}


        and mbrTakeOrder.take_capacity = #{takeCapacity}


        and mbrTakeOrder.delivery_mode = #{deliveryMode}


        and mbrTakeOrder.package_price = #{packagePrice}


        and mbrTakeOrder.total_package_amt = #{totalPackageAmt}


        and mbrTakeOrder.pay_total_amt = #{payTotalAmt}


        and mbrTakeOrder.pay_real_amt = #{payRealAmt}


        and mbrTakeOrder.order_status = #{orderStatus}


        and mbrTakeOrder.shipping_detail_addr = #{shippingDetailAddr}


        and mbrTakeOrder.shipping_province_id = #{shippingProvinceId}


        and mbrTakeOrder.shipping_city_id = #{shippingCityId}


        and mbrTakeOrder.shipping_county_id = #{shippingCountyId}


        and mbrTakeOrder.transaction_no = #{transactionNo}


        and mbrTakeOrder.order_no = #{orderNo}


        and mbrTakeOrder.create_date = #{createDate}


        and mbrTakeOrder.modify_date = #{modifyDate}
