mapper.select:
select 

from m_mbr_wine_custom_detail MbrWineCustomDetail
where MbrWineCustomDetail.id = #{id}

select 

from m_mbr_wine_custom_detail MbrWineCustomDetail

    
        and MbrWineCustomDetail.wine_custom_id = #{wineCustomId}
    
    
        and MbrWineCustomDetail.wine_custom_element_id = #{wineCustomElementId}
    
    
        and MbrWineCustomDetail.mbr_wine_custom_id = #{mbrWineCustomId}
    
    
        and MbrWineCustomDetail.mbr_id = #{mbrId}
    
    
        and MbrWineCustomDetail.bg_img = #{bgImg}
    
    
        and MbrWineCustomDetail.mask_img = #{maskImg}
    
    
        and MbrWineCustomDetail.preview_img = #{previewImg}
    
    
        and MbrWineCustomDetail.bottom_img = #{bottomImg}
    
    
        and MbrWineCustomDetail.print_img = #{printImg}
    
    
        and MbrWineCustomDetail.create_date = #{createDate}
    
    
        and MbrWineCustomDetail.modify_date = #{modifyDate}
