mapper.select:
select 

from m_mbr_wine_custom MbrWineCustom
where MbrWineCustom.id = #{id}

select 

from m_mbr_wine_custom MbrWineCustom

    
        and MbrWineCustom.mbr_wine_custom_order_id = #{mbrWineCustomOrderId}
    
    
        and MbrWineCustom.wine_custom_id = #{wineCustomId}
    
    
        and MbrWineCustom.win_custom_id = #{winCustomId}
    
    
        and MbrWineCustom.winery_id = #{wineryId}
    
    
        and MbrWineCustom.mbr_id = #{mbrId}
    
    
        and MbrWineCustom.custom_cnt = #{customCnt}
    
    
        and MbrWineCustom.custom_price = #{customPrice}
    
    
        and MbrWineCustom.custom_total_amt = #{customTotalAmt}
    
    
        and MbrWineCustom.custom_name = #{customName}
    
    
        and MbrWineCustom.sku_name = #{skuName}
    
    
        and MbrWineCustom.create_date = #{createDate}
    
    
        and MbrWineCustom.modify_date = #{modifyDate}
