mapper.select:
select

from m_mbr_wine_custom_order mco
where mco.id = #{id}

select

from m_mbr_wine_custom_order mco
where mco.order_no = #{orderNo}

select

from m_mbr_wine_custom_order mco

    
        and mco.winery_id = #{wineryId}
    
    
        and mco.mbr_id = #{mbrId}
    
    
        and mco.custom_total_cnt = #{customTotalCnt}
    
    
        and mco.pay_total_amt = #{payTotalAmt}
    
    
        and mco.pay_real_amt = #{payRealAmt}
    
    
        and mco.order_status = #{orderStatus}
    
    
        and mco.shipping_detail_addr = #{shippingDetailAddr}
    
    
        and mco.shipping_province_id = #{shippingProvinceId}
    
    
        and mco.shipping_city_id = #{shippingCityId}
    
    
        and mco.shipping_county_id = #{shippingCountyId}
    
    
        and mco.shipping_person_name = #{shippingPersonName}
    
    
        and mco.shipping_person_phone = #{shippingPersonPhone}
    
    
        and mco.transaction_no = #{transactionNo}
    
    
        and mco.order_no = #{orderNo}
    
    
        and mco.pay_date = #{payDate}
    
    
        and mco.create_date = #{createDate}
    
    
        and mco.modify_date = #{modifyDate}
