mapper.select:
select 

from m_mbr_wine_custom_order MbrWineCustomOrder
where MbrWineCustomOrder.id = #{id}

select 

from m_mbr_wine_custom_order MbrWineCustomOrder

    
        and MbrWineCustomOrder.winery_id = #{wineryId}
    
    
        and MbrWineCustomOrder.mbr_id = #{mbrId}
    
    
        and MbrWineCustomOrder.custom_total_cnt = #{customTotalCnt}
    
    
        and MbrWineCustomOrder.pay_total_amt = #{payTotalAmt}
    
    
        and MbrWineCustomOrder.pay_real_amt = #{payRealAmt}
    
    
        and MbrWineCustomOrder.order_status = #{orderStatus}
    
    
        and MbrWineCustomOrder.shipping_detail_addr = #{shippingDetailAddr}
    
    
        and MbrWineCustomOrder.shipping_province_id = #{shippingProvinceId}
    
    
        and MbrWineCustomOrder.shipping_city_id = #{shippingCityId}
    
    
        and MbrWineCustomOrder.shipping_county_id = #{shippingCountyId}
    
    
        and MbrWineCustomOrder.shipping_person_name = #{shippingPersonName}
    
    
        and MbrWineCustomOrder.shipping_person_phone = #{shippingPersonPhone}
    
    
        and MbrWineCustomOrder.transaction_no = #{transactionNo}
    
    
        and MbrWineCustomOrder.order_no = #{orderNo}
    
    
        and MbrWineCustomOrder.create_date = #{createDate}
    
    
        and MbrWineCustomOrder.modify_date = #{modifyDate}
