mapper.select:
select 

from m_mbr_winery_activity_sign mbrWineryActivitySign


        and mbrWineryActivitySign.winery_activity_id = #{wineryActivityId}


        and mbrWineryActivitySign.mbr_id = #{mbrId}


        and mbrWineryActivitySign.mbr_act_status = #{mbrActStatus}


        and mbrWineryActivitySign.sign_up_date = #{signUpDate}


        and mbrWineryActivitySign.create_date = #{createDate}


        and mbrWineryActivitySign.modify_date = #{modifyDate}


        and mbrWineryActivitySign.sign_in_date = #{signInDate}


        and mbrWineryActivitySign.sign_off_date = #{signOffDate}
