mapper.select:
select

from m_mbr_winery_voucher mwv
where mwv.id = #{id}

select

from m_mbr_winery_voucher mwv


        and mwv.winery_id = #{wineryId}


        and mwv.winery_voucher_inst_id = #{wineryVoucherInstId}


        and mwv.mbr_id = #{mbrId}


        and mwv.name = #{name}


        and mwv.type = #{type}


        and mwv.voucher_amt = #{voucherAmt}


        and mwv.voucher_discount = #{voucherDiscount}


        and mwv.prod_sku_id = #{prodSkuId}


        and mwv.enable_type = #{enableType}


        and mwv.enable_amt = #{enableAmt}


        and mwv.quantity_limit_cnt = #{quantityLimitCnt}


        and mwv.effective_begin_date = #{effectiveBeginDate}


        and mwv.effective_end_date = #{effectiveEndDate}


        and mwv.use_scope = #{useScope}


        and mwv.voucher_cnt = #{voucherCnt}


        and mwv.create_date = #{createDate}


        and mwv.modify_date = #{modifyDate}
