mapper.insert:
insert into m_member (id, mbr_level_id, winery_id, token, nick_name, phone, wechat,
    open_id, user_icon, STATUS, status_time, total_integral, total_store_remain,
    total_store_increment, market_qr_code, market_pid, acct_balance, create_date, modify_date
    )
values (#{id}, #{mbrLevelId}, #{wineryId}, #{token}, #{nickName}, #{phone}, #{wechat},
    #{openId}, #{userIcon}, #{status}, #{statusTime}, #{totalIntegral}, #{totalStoreRemain},
    #{totalStoreIncrement}, #{marketQrCode}, #{marketPid}, #{acctBalance}, #{createDate}, #{modifyDate}
    )

mapper.select:
select

from m_member member


        and member.mbr_level_id = #{mbrLevelId}


        and member.winery_id = #{wineryId}


        and member.token = #{token}


        and member.nick_name = #{nickName}


        and member.phone = #{phone}


        and member.wechat = #{wechat}


        and member.open_id = #{openId}


        and member.user_icon = #{userIcon}


        and member.STATUS = #{status}


        and member.status_time = #{statusTime}


        and member.total_integral = #{totalIntegral}


        and member.total_store_remain = #{totalStoreRemain}


        and member.total_store_increment = #{totalStoreIncrement}


        and member.market_qr_code = #{marketQrCode}


        and member.market_pid = #{marketPid}


        and member.acct_balance = #{acctBalance}


        and member.create_date = #{createDate}


        and member.modify_date = #{modifyDate}
