mapper.select:
select

from m_member mbr
where mbr.id = #{id}

select

from m_member mbr


        and mbr.mbr_level_id = #{mbrLevelId}


        and mbr.winery_id = #{wineryId}


        and mbr.mbr_name = #{mbrName}


        and mbr.phone = #{phone}


        and mbr.open_id = #{openId}


        and mbr.user_icon = #{userIcon}


        and mbr.status = #{status}


        and mbr.status_time = #{statusTime}


        and mbr.total_integral = #{totalIntegral}


        and mbr.total_store_remain = #{totalStoreRemain}


        and mbr.total_store_increment = #{totalStoreIncrement}


        and mbr.market_qr_code = #{marketQrCode}


        and mbr.market_pid = #{marketPid}


        and mbr.acct_balance = #{acctBalance}


        and mbr.age = #{age}


        and mbr.gender = #{gender}


        and mbr.invite_return_amt = #{inviteReturnAmt},


        and mbr.recharge_return_amt = #{rechargeReturnAmt},


        and mbr.create_date = #{createDate}


        and mbr.modify_date = #{modifyDate}
