mapper.select:
select 

from s_prod p


        and p.winery_id = #{wineryId}


        and p.prod_category_id = #{prodCategoryId}


        and p.lable_type = #{lableType}


        and p.prod_name = #{prodName}


        and p.prod_title = #{prodTitle}


        and p.prod_code = #{prodCode}


        and p.total_stock_cnt = #{totalStockCnt}


        and p.list_img = #{listImg}


        and p.descri = #{descri}


        and p.is_limit = #{isLimit}


        and p.limit_count = #{limitCount}


        and p.prod_status = #{prodStatus}


        and p.is_recommend = #{isRecommend}


        and p.admin_id = #{adminId}


        and p.is_del = #{isDel}


        and p.create_date = #{createDate}


        and p.modify_date = #{modifyDate}
