mapper.select:
select

from b_wine_cellar_activity_detail wcad
where wcad.id = #{id}

select

from b_wine_cellar_activity_detail wcad


        and wcad.wine_cellar_activity = #{wineCellarActivity}


        and wcad.detail_title = #{detailTitle}


        and wcad.detail_brief = #{detailBrief}


        and wcad.detail_img = #{detailImg}


        and wcad.detail_status = #{detailStatus}


        and wcad.sort = #{sort}


        and wcad.create_date = #{createDate}


        and wcad.modify_date = #{modifyDate}
