mapper.select:
select 

from b_wine_cellar_activity_detail d
where d.id = #{id}

select 

from b_wine_cellar_activity_detail d


        and d.wine_cellar_activity_id = #{wineCellarActivityId}


        and d.prod_id = #{prodId}


        and d.prod_sku_id = #{prodSkuId}


        and d.winery_voucher_id = #{wineryVoucherId}


        and d.present_type = #{presentType}


        and d.present_remark = #{presentRemark}


        and d.sort = #{sort}


        and d.create_date = #{createDate}


        and d.modify_date = #{modifyDate}

select d.id dId,d.present_remark presentRemark,s.id sId, s.sku_name skuName,s.sku_sell_price skuSellPrice,s.alcohol_cnt alcoholCnt,s.fragrance_type fragranceType from b_wine_cellar_activity_detail d
inner join s_prod_sku s on d.prod_sku_id=s.id
where d.id=#{id}
