mapper.select:
select 

from b_wine_cellar_activity a
where a.id = #{id}

mapper.insert:
insert into b_wine_cellar_activity (id, winery_id, act_title, act_cover_img, act_breif, act_detail_img, 
    is_platform, is_now, take_date, like_total_cnt, share_total_cnt, act_status, 
    present_detail, is_increase, increase_scale, create_date, modify_date
    )
values (#{id}, #{wineryId}, #{actTitle}, #{actCoverImg}, #{actBreif}, #{actDetailImg}, 
    #{isPlatform}, #{isNow}, #{takeDate}, #{likeTotalCnt}, #{shareTotalCnt}, #{actStatus}, 
    #{presentDetail}, #{isIncrease}, #{increaseScale}, #{createDate}, #{modifyDate}
    )

mapper.select:
select 

from b_wine_cellar_activity a


        and a.winery_id = #{wineryId}


        and a.act_title = #{actTitle}


        and a.act_cover_img = #{actCoverImg}


        and a.act_breif = #{actBreif}


        and a.act_detail_img = #{actDetailImg}


        and a.is_platform = #{isPlatform}


        and a.is_now = #{isNow}


        and a.take_date = #{takeDate}


        and a.like_total_cnt = #{likeTotalCnt}


        and a.share_total_cnt = #{shareTotalCnt}


        and a.act_status = #{actStatus}


        and a.present_detail = #{presentDetail}


        and a.is_increase = #{isIncrease}


        and a.increase_scale = #{increaseScale}


        and a.create_date = #{createDate}


        and a.modify_date = #{modifyDate}

select a.*,ifnull(l.like_status,0) like_status from b_wine_cellar_activity a
left join b_wine_cellar_activity_like l on a.id = l.winery_id and l.mbr_id=#{mbrId}
where a.id = #{id}

select a.act_cover_img,a.act_title,a.like_total_cnt,a.share_total_cnt,ifnull(l.like_status,0) like_status from b_wine_cellar_activity a
left join b_wine_cellar_activity_like l on a.id = l.winery_id and l.mbr_id=#{mbrId}
where a.act_status = 2
