mapper.select:
select 

from b_wine_cellar_activity_prod wcap
where wcap.id = #{id}

select 

from b_wine_cellar_activity_prod wcap


        and wcap.wine_cellar_activity_id = #{wineCellarActivityId}


        and wcap.prod_id = #{prodId}


        and wcap.prod_sku_id = #{prodSkuId}


        and wcap.winery_voucher_id = #{wineryVoucherId}


        and wcap.present_type = #{presentType}


        and wcap.winery_voucher_inst = #{wineryVoucherInst}


        and wcap.return_amt = #{returnAmt}


        and wcap.present_remark = #{presentRemark}


        and wcap.sort = #{sort}


        and wcap.create_date = #{createDate}


        and wcap.modify_date = #{modifyDate}
