mapper.select:
select 

from b_wine_custom_element WineCustomElement
where WineCustomElement.id = #{id}

select 

from b_wine_custom_element WineCustomElement

    
        and WineCustomElement.element_name = #{elementName}
    
    
        and WineCustomElement.element_code = #{elementCode}
    
    
        and WineCustomElement.element_icon = #{elementIcon}
    
    
        and WineCustomElement.element_status = #{elementStatus}
    
    
        and WineCustomElement.sort = #{sort}
    
    
        and WineCustomElement.create_date = #{createDate}
    
    
        and WineCustomElement.modify_date = #{modifyDate}
