mapper.select:
select

from b_wine_custom_element wce
where wce.id = #{id}

select

from b_wine_custom_element wce


        and wce.winery_id = #{wineryId}


        and wce.element_name = #{elementName}


        and wce.element_code = #{elementCode}


        and wce.element_icon = #{elementIcon}


        and wce.element_status = #{elementStatus}


        and wce.sort = #{sort}


        and wce.create_date = #{createDate}


        and wce.modify_date = #{modifyDate}

select group_concat(wce.element_name) element_name
from b_wine_custom_element_content wcec join b_wine_custom_element wce
on wcec.wine_custom_element_id = wce.id and wcec.wine_custom_id = #{id}

select

from b_wine_custom_element wce where wce.id in

    #{id}

select

from b_wine_custom_element_content wcec join b_wine_custom_element wce
on wcec.wine_custom_element_id = wce.id and wcec.wine_custom_id = #{id}
