mapper.select:
select 

from b_winery_activity_detail wad
where wad.id = #{id}

select 

from b_winery_activity_detail wad


        and wad.winery_activity_id = #{wineryActivityId}


        and wad.prod_sku_id = #{prodSkuId}


        and wad.winery_voucher_id = #{wineryVoucherId}


        and wad.detail_title = #{detailTitle}


        and wad.detail_brief = #{detailBrief}


        and wad.detail_img = #{detailImg}


        and wad.sort = #{sort}


        and wad.create_date = #{createDate}


        and wad.modify_date = #{modifyDate}
