mapper.select:
select

from b_winery_activity a
where a.id = #{id}

mapper.insert:
insert into b_winery_activity (id, winery_id, act_title, act_cover_img, is_platform, act_type, 
    sign_start_date, sign_end_date, act_start_date, act_end_date, like_total_cnt, 
    share_total_cnt, sign_total_cnt, create_date, modify_date, act_status
    )
values (#{id}, #{wineryId}, #{actTitle}, #{actCoverImg}, #{isPlatform}, #{actType}, 
    #{signStartDate}, #{signEndDate}, #{actStartDate}, #{actEndDate}, #{likeTotalCnt}, 
    #{shareTotalCnt}, #{signTotalCnt}, #{createDate}, #{modifyDate}, #{actStatus}
    )

mapper.select:
select 

from b_winery_activity wineryActivity


        and wineryActivity.id = #{id}


        and wineryActivity.winery_id = #{wineryId}


        and wineryActivity.act_title = #{actTitle}


        and wineryActivity.act_cover_img = #{actCoverImg}


        and wineryActivity.is_platform = #{isPlatform}


        and wineryActivity.act_type = #{actType}


        and wineryActivity.sign_start_date = #{signStartDate}


        and wineryActivity.sign_end_date = #{signEndDate}


        and wineryActivity.act_start_date = #{actStartDate}


        and wineryActivity.act_end_date = #{actEndDate}


        and wineryActivity.like_total_cnt = #{likeTotalCnt}


        and wineryActivity.share_total_cnt = #{shareTotalCnt}


        and wineryActivity.sign_total_cnt = #{signTotalCnt}


        and wineryActivity.create_date = #{createDate}


        and wineryActivity.modify_date = #{modifyDate}


        and wineryActivity.act_status = #{actStatus}

select a.*,ifnull(s.mbr_act_status,0) mbr_act_status from b_winery_activity a
left join m_mbr_winery_activity_sign s on a.id=s.winery_activity_id and s.mbr_id=#{mbrId}
where act_status = 2 and act_end_date  >=  now()

select a.*,ifnull(l.like_status,0) like_status,ifnull(s.mbr_act_status,0) mbr_act_status from b_winery_activity a
left join b_winery_activity_like l on a.id = l.winery_id and l.mbr_id=#{mbrId}
left join m_mbr_winery_activity_sign s on a.id=s.winery_activity_id and s.mbr_id=#{mbrId}
where a.id = #{id}

select a.*,ifnull(l.like_status,0) like_status,ifnull(s.mbr_act_status,0) mbr_act_status from b_winery_activity a
left join b_winery_activity_like l on a.id = l.winery_id and l.mbr_id=#{mbrId}
left join m_mbr_winery_activity_sign s on a.id=s.winery_activity_id and s.mbr_id=#{mbrId}
where a.act_status = 2
