mapper.select:
select 

from b_winery_activity l
where l.id = #{id}

select 

from b_winery_activity l


        and l.winery_id = #{wineryId}


        and l.act_title = #{actTitle}


        and l.act_cover_img = #{actCoverImg}


        and l.is_platform = #{isPlatform}


        and l.act_type = #{actType}


        and l.sign_start_date = #{signStartDate}


        and l.sign_end_date = #{signEndDate}


        and l.act_start_date = #{actStartDate}


        and l.act_end_date = #{actEndDate}


        and l.like_total_cnt = #{likeTotalCnt}


        and l.share_total_cnt = #{shareTotalCnt}


        and l.sign_total_cnt = #{signTotalCnt}


        and l.act_status = #{actStatus}


        and l.is_sign = #{isSign}


        and l.activity_province_id = #{activityProvinceId}


        and l.activity_city_id = #{activityCityId}


        and l.activity_county_id = #{activityCountyId}


        and l.activity_detail_addr = #{activityDetailAddr}


        and l.activity_full_addr = #{activityFullAddr}


        and l.create_date = #{createDate}


        and l.modify_date = #{modifyDate}

select a.*,ifnull(s.mbr_act_status,0) mbr_act_status from b_winery_activity a
left join m_mbr_winery_activity_sign s on a.id=s.winery_activity_id and s.mbr_id=#{mbrId}
where act_status = 2 and act_end_date  >=  now()

select a.*,ifnull(l.like_status,0) like_status,ifnull(s.mbr_act_status,0) mbr_act_status from b_winery_activity a
left join b_winery_activity_like l on a.id = l.winery_id and l.mbr_id=#{mbrId}
left join m_mbr_winery_activity_sign s on a.id=s.winery_activity_id and s.mbr_id=#{mbrId}
where a.id = #{id}

select a.*,ifnull(l.like_status,0) like_status,ifnull(s.mbr_act_status,0) mbr_act_status from b_winery_activity a
left join b_winery_activity_like l on a.id = l.winery_id and l.mbr_id=#{mbrId}
left join m_mbr_winery_activity_sign s on a.id=s.winery_activity_id and s.mbr_id=#{mbrId}
where a.act_status = 2
