mapper.select:
select

from b_winery_adjust_advance waa
where waa.id = #{id}

select

from b_winery_adjust_advance waa


        and waa.winery_adjust_wine_id = #{wineryAdjustWineId}


        and waa.advance_name = #{advanceName}


        and waa.advance_img = #{advanceImg}


        and waa.advance_remark = #{advanceRemark}


        and waa.status = #{status}


        and waa.sort = #{sort}


        and waa.create_date = #{createDate}


        and waa.modify_date = #{modifyDate}

select

from b_winery_adjust_advance waa

    waa.winery_adjust_wine_id = #{adjustId}
    and waa.status = #{status}

order by sort
