mapper.select:
select

from b_winery_master wm
where wm.id = #{id}

select
,
ml.name as mbr_level_name
from b_winery_master wm
left join m_member m on m.id = wm.mbr_id
left join m_mbr_level ml on ml.id = m.mbr_level_id


        and wm.mbr_id = #{mbrId}


        and wm.winery_id = #{wineryId}


        and wm.master_type = #{masterType}


        and wm.master_name = #{masterName}


        and wm.master_detail = #{masterDetail}


        and wm.head_photo = #{headPhoto}


        and wm.create_date = #{createDate}


        and wm.modify_date = #{modifyDate}
