mapper.select:
select 

from b_winery_sight_mbr_like wsml
where wsml.id = #{id}

select 

from b_winery_sight_mbr_like wsml


        and wsml.mbr_id = #{mbrId}


        and wsml.like_status = #{likeStatus}


       and wsml.winery_sight_id = #{winerySightId}


        and wsml.winery_id = #{wineryId}


        and wsml.create_date = #{createDate}


        and wsml.modify_date = #{modifyDate}
